					$(this).find('.ans-control .extension_place').append(tmpl('AnswerEditPlugin-Control', {uid: uid, aid: aid}));
					$(this).find('.ans-extension-area').append(tmpl('AnswerEditPlugin-Area', {uid: uid, aid: aid}));
